(function($){$.fn.balanceLines=function(){var balanceLinesGetPadding=function(block){var balanceHeight=block.height();var balanceContent=block.html();var minPad=0;var maxPad=100;var testPad=null;block.append('<div id="balanceTemporaryContainer" style="position:absolute; display:block; visibility:hidden;"></div>');balanceTemp=$("#balanceTemporaryContainer");balanceTemp.html(balanceContent);balanceTemp.width(block.width());while(minPad<maxPad){testPad=minPad+Math.ceil((maxPad-minPad)/2);balanceTemp.css("padding-right",testPad+"%");if(balanceTemp.height()>balanceHeight){maxPad=maxPad==testPad?testPad-1:testPad}else{minPad=testPad}}balanceTemp.remove();return maxPad-1};this.each(function(){var block=$(this);block.css("padding-left","");block.css("padding-right","");block.css("position","relative");var balancePadding=balanceLinesGetPadding(block);if(block.css("text-align")=="right"){block.css("padding-left",balancePadding+"%")}else if(block.css("text-align")=="center"){block.css("padding-left",balancePadding/2+"%");block.css("padding-right",balancePadding/2+"%")}else{block.css("padding-right",balancePadding+"%")}});return this}})(jQuery);
